import React from 'react';
import { Carousel } from 'antd';
import "../Carousel/carousel.css"
import TCard from '../TCard';
const contentStyle = {
    height: '120px',
    color: '#fff',
    // lineHeight: '160px',
    textAlign: 'start',
    // background: '#364d79',
    color: '#000'
};

const data = [
    {
        description: "Software Engineer with expertise in web development",
        name: "John Doe",
        companyName: "Tech Innovations Inc."
    },
    {
        description: "Graphic Designer specializing in brand identity",
        name: "Jane Smith",
        companyName: "Creative Designs Co."
    },
    {
        description: "Experienced Project Manager with a focus on efficiency",
        name: "Robert Johnson",
        companyName: "Global Solutions Ltd."
    },
    {
        description: "Marketing Specialist skilled in digital marketing strategies",
        name: "Emily Davis",
        companyName: "MarketPro Strategies"
    },
    {
        description: "Data Scientist with a passion for analytics and machine learning",
        name: "Alex Turner",
        companyName: "Data Insights Corporation"
    }
];

const CarouselSlider = () => (
    <>
        <div className='mx-auto w-1/4 pt-12'>
            <div className='text-center text-2xl font-bold'>
                <h2>What Our Customer Says</h2>
            </div>
            <Carousel className=' pt-2'>
            {data.map((card)=>{
                return
            })}

            </Carousel>
        </div>
    </>
);
export default CarouselSlider;
