import React from "react";
import "./../../App.css";

const CHOOSE_US_IMAGES = [
  "images/Frame(3).svg",
  "images/Frame (1).svg",
  "images/Frame (2).svg",
];

const ChooseUsImage = ({ src }) => (
  <div className="bg-[#F5F5F5] rounded-[28px] p-8 w-44 flex items-center justify-center">
    <img src={src} alt="" />
  </div>
);

function ChooseUs() {
  return (
    <div className="items-center flex flex-col flex-wrap justify-between py-6 px-7 size-full md:flex md:flex-col lg:items-center lg:flex-wrap lg:justify-center">
      <div className="lg:flex lg:flex-row lg:items-center">
        <div className="md:px-24 lg:px-20">
          <h2 className="text-3xl font-bold">Why choose us for moving?</h2>
          <p className="pt-6 text-justify md: text-1xl lg:flex lg:flex-row">
            Choose us for your moving needs because we prioritize your peace of mind. Our experienced team is dedicated to ensuring a seamless and stress-free relocation process.
          </p>
        </div>
        <div className="flex flex-wrap sm:flex justify-evenly pt-4 basis-1/2 gap-6 md:gap-0 md:flex md:flex-row lg:flex lg:flex-nowrap lg:gap-4 box-sha">
          {CHOOSE_US_IMAGES.map((image, index) => (
            <ChooseUsImage key={index} src={image} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default ChooseUs;
